    private ManualValidator() {
        throw new UnsupportedOperationException();
    }

    public static final void notNull(Object obj) {
        if (obj == null) {
            throw new BadRequestException("input parameter is null");
        }
    }

    public static final void notEmpty(String str, boolean trim) {
        notNull(str);
        String compareStr = trim ? str.trim() : str;
        if (compareStr.isEmpty()) {
            throw new BadRequestException("String is empty");
        }
    }

    public static final void validEmail(String email) {
        notNull(email);
        if (!PATTERN_EMAIL.matcher(email).matches()) {
            throw new BadRequestException("invalid email pattern: " + email);
        }
    }

    public static <T> void validate(T obj) {
        notNull(obj);

        Validator validator = Validation.buildDefaultValidatorFactory().getValidator();
        Set<ConstraintViolation<T>> errors = validator.validate(obj);

        if (!errors.isEmpty()) {
            throw new BadRequestException(obj.getClass() + ": " + errors.iterator().next().getMessage());
        }
    }
